    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {
        DadosPessoais dadoAtual = new DadosPessoais();

        jrbMasculino.setActionCommand("1");
        jrbFeminino.setActionCommand("2");

        dadoAtual.setNome(txtfNome.getText().toString());
        dadoAtual.setCPF(txtfCPF.getText().toString());
        dadoAtual.setSexo(Integer.parseInt(grupoSexo.getSelection().getActionCommand()));
        dadoAtual.setPeso(Double.parseDouble(txtPeso.getText()));
        dadoAtual.setAltura(Double.parseDouble(txtAltura.getText()));
        dadoAtual.setIdade(Integer.parseInt(txtIdade.getText()));

        TerceiraTela terceiraTela = new TerceiraTela();
        terceiraTela.dadoAtual = dadoAtual;
        terceiraTela.setVisible(true);
        this.setVisible(false);
    }

